{"filter":false,"title":"145-jukebox.rb","tooltip":"/method.each/145-jukebox.rb","undoManager":{"mark":2,"position":2,"stack":[[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":0,"column":0},"end":{"row":0,"column":1}},"text":"√"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":0,"column":0},"end":{"row":0,"column":1}},"text":"√"}]}],[{"group":"doc","deltas":[{"action":"insertText","range":{"start":{"row":0,"column":0},"end":{"row":0,"column":75}},"text":"# Create a Jukebox program that takes four commands: list, play, help, exit"},{"action":"insertText","range":{"start":{"row":0,"column":75},"end":{"row":1,"column":0}},"text":"\n"},{"action":"insertLines","range":{"start":{"row":1,"column":0},"end":{"row":59,"column":0}},"lines":["# and execute each of the commands. ","# The list command should execute ","# When you execute play, you should also be able to specify which song.","# You can make this as fancy or as simple as you'd like. ","","songs = [","  \"The Phoenix - 1901\",","  \"Tokyo Police Club - Wait Up\",","  \"Sufjan Stevens - Too Much\",","  \"The Naked and the Famous - Young Blood\",","  \"(Far From) Home - Tiga\",","  \"The Cults - Abducted\",","  \"The Phoenix - Consolation Prizes\"","]","puts \"Welcome to Jukebox 1000\"","puts ","puts \"Please select from the following commands:\"","puts \"list, play, help, exit\"","puts ","command = \"\"","while command != \"exit\"","  ","  command = gets.chomp.downcase","  if command ==\"list\"","    songs.each_with_index {|song, index| puts \"#{index+1}. #{song}\"}","    puts \"Would you like to listen to anything?\"","    puts \"Please say a command.\"","    ","  elsif command.start_with?(\"play\")","    puts \"Which song number do you want to play?\"","    songs.each_with_index {|song, index| puts \"#{index+1}. #{song}\"}","      nonexistent_number = true","      while nonexistent_number","        song_number = gets.chomp.to_i","        if songs.size < song_number","          puts \"This song choice does not exist.\"","          puts \"Please make a different selection.\"","        else","          nonexistent_number= false","          puts \"Now playing... #{song_number}. #{songs[song_number-1]}\"","          puts \"Enjoy!\"","        end","      end","  elsif command.start_with?(\"help\")","    puts \"Command functions:\"","    puts \"List: lists all songs available in jukebox\"","    puts \"Play: select a song number from song list to play\"","    puts \"Help: lists command functions\"","    puts \"Exit: exits out of Jukebox 1000\"","    puts \"Say a command\"","  else","    puts \"Thanks for listening!\" ","    break","  end  ","  ","end","puts \"Hope you enjoyed our songs. To add to this song selection, please visit our website at www.jukebox1000.com.\"",""]},{"action":"insertText","range":{"start":{"row":59,"column":0},"end":{"row":59,"column":81}},"text":"# elsif command.include?(\"play\") #what's wrong with this? include vs start with??"}]}]]},"ace":{"folds":[],"scrolltop":284.7272415161133,"scrollleft":113,"selection":{"start":{"row":59,"column":81},"end":{"row":59,"column":81},"isBackwards":false},"options":{"guessTabSize":true,"useWrapMode":false,"wrapToView":true},"firstLineState":{"row":18,"state":"start","mode":"ace/mode/ruby"}},"timestamp":1411154210952,"hash":"81d3cbedcb6ece5ea76528296a8360417ab7ae8d"}