          return \"ERROR. The number must be a positive integer.\"","        else","            roman_num = \"M\" * (num / 1000)","            roman_num = roman_num + \"D\" * (num % 1000 / 500)","            roman_num = roman_num + \"C\" * (num % 500 / 100)","            roman_num = roman_num + \"L\" * (num % 100 / 50)","            roman_num = roman_num + \"X\" * (num % 50 / 10)","            roman_num = roman_num + \"V\" * (num % 10 / 5)","            roman_num = roman_num + \"I\" * (num % 5 / 1)","            ","            roman_num.gsub!(\"DCCCC\", \"CM\")","            roman_num.gsub!(\"CCCC\", \"CD\")","            roman_num.gsub!(\"LXXXX\", \"XC\")","            roman_num.gsub!(\"XXXX\", \"XL\")","            roman_num.gsub!(\"VIIII\", \"IX\")","            roman_num.gsub!(\"IIII\", \"IV\")","            return roman_num","        end","    end","end","","def roman_input","    puts","    puts \"Please enter a number so that I can convert it to modern Roman: \"","    num_entered = gets.chomp.to_f","    roman_output = roman_numeral (num_entered)","    puts \"In modern Roman, the number will be represented as: #{roman_output}\"","    puts","end",""]},{"action":"insertText","range":{"start":{"row":38,"column":0},"end":{"row":38,"column":11}},"text":"roman_input"}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":5,"column":0},"end":{"row":5,"column":8}},"text":"        "},{"action":"removeLines","range":{"start":{"row":4,"column":0},"end":{"row":5,"column":0}},"nl":"\n","lines":["    while true "]}]}],[{"group":"doc","deltas":[{"action":"removeLines","range":{"start":{"row":25,"column":0},"end":{"row":26,"column":0}},"nl":"\n","lines":["    end"]}]}],[{"group":"doc","deltas":[{"action":"removeText","range":{"start":{"row":3,"column":21},"end":{"row":4,"column":0}},"text":"\n"}]}]]},"ace":{"folds":[],"scrolltop":151.5,"scrollleft":0,"selection":{"start":{"row":16,"column":12},"end":{"row":21,"column":41},"isBackwards":true},"options":{"g